    const data = await api_call.json();
    console.log(city);
    console.log(country);
    console.log(data);*/

    const request_url = `https://www.mapquestapi.com/geocoding/v1/address?key=${API_KEY_LOCATION}&inFormat=kvp&outFormat=json&location=${city}%2C+${country}&thumbMaps=false`
    const api_call = await fetch(request_url);
    const data = await api_call.json();
    console.log(request_url);
  }

  render() {
    return (
      <div>
        <Titles />
        <Form getLocation={this.getLocation}/>
        <Sunset />
      </div>
    );
  }
};

export default App;
